#include <bits/stdc++.h>

using namespace std;

const int maxn = 18;
const int maxm = 18;
const int inf = 0x3f3f3f3f;
char mp[maxn][maxn];
typedef pair<int, int> P;
P in[maxm];
P out[maxm];
int n, m;
int d[maxn][maxn];
int sx, sy;
int dx[4] = {1, 0, -1, 0};
int dy[4] = {0, 1, 0, -1};
