``` C++
#include <bits/stdc++.h>

using namespace std;

const int maxn = 1e5 + 7;
typedef pair<int, int> P;
P d[maxn];
int t;
int n, m;
map<int, int> mp;

int main(){
    //ios::sync_with_stdio(false);
    //cin.tie(0);
    //cout.tie(0);
    #ifndef ONLINE_JUDGE
        freopen("in.txt", "r", stdin);
    #endif

    scanf("%d", &t);
    while(t--){
        mp.clear();
        scanf("%d%d", &n, &m);
        int tmp;
        for(int i = 0; i < n; i++){
            scanf("%d", &tmp);
            mp[tmp]++;
        }

        int cnt = 0;
        for(auto x : mp){
            d[cnt].first = x.first;
            d[cnt].second = x.second;
            cnt++;
        }

        int flag = 0;
        int rem = n - m;//
        int i;
        for(i = cnt - 1; i >= 0; i--){
            int res = rem - d[i].second;
            if(res <= 0){
                flag = 1;
                break;
            }
            for(int j = 0; j < cnt; j++){
                if(j == i) continue;
                res -= min(d[i].second - 1, d[j].second);
                if(res <= 0){
                    flag = 1;
                    break;
                }
            }
            if(flag) break;
        }

        if(flag) printf("%d\n", d[i].first);
        else printf("-1\n");
    }

    return 0;
}
```

