```C++
#include <bits/stdc++.h>

using namespace std;

const int maxn = 18;
const int maxm = 18;
const int inf = 0x3f3f3f3f;
char mp[maxn][maxn];
typedef pair<int, int> P;
P in[maxm];
P out[maxm];
int n, m;
int d[maxn][maxn];
int sx, sy;
int dx[4] = {1, 0, -1, 0};
int dy[4] = {0, 1, 0, -1};
int dis[maxm][maxm];
int dp[1 <<  maxm][maxm];

//DP
void dpsolve(){
    for(int S = 0; S < 1 << m; S++){
        fill(dp[S], dp[S] + m, inf);
    }
    for(int i = 0; i < m; i++){
        dp[(1 << m) - 1][i] = 0;//
    }
    for(int S = (1 << m) - 2; S >= 0; S--){
        for(int v = 0; v < m; v++){
            for(int u = 0; u < m; u++){
                if(!(S >> u & 1)){
                    dp[S][v] = min(dp[S][v], dp[S | 1 << u][u] + dis[v][u]);
                }
            }
        }
    }
    int res = inf;
    for(int i = 0; i < m; i++){
        res = min(res, dp[1 << i][i]);//
    }
    if(res == inf) printf("%d\n", -1);
    else printf("%d\n", res);
}

//bfs
void bfs(int sx, int sy){
    queue<P> que;
    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            d[i][j] = inf;
        }
    }
    d[sx][sy] = 0;
    que.push(P(sx, sy));

    while(!que.empty()){
        P p = que.front();
        que.pop();
        for(int i = 0; i < 4; i++){
            int nx = p.first + dx[i];
            int ny = p.second + dy[i];
            if(nx >= 0 && nx < n && ny >= 0 && ny < n && mp[nx][ny] != '#' && d[nx][ny] == inf){

                d[nx][ny] = d[p.first][p.second] + 1;
                que.push(P(nx, ny));
            }
        }

    }
}

int main(){
    #ifndef ONLINE_JUDGE
        freopen("in.txt", "r", stdin);
    #endif // ONLINE_JUDGE
    while(~scanf("%d%d", &n, &m)){
        for(int i = 0; i < n; i++){
            scanf("%s", mp[i]);
        }
        for(int i = 0; i < m; i++){
            scanf("%d%d%d%d", &in[i].first, &in[i].second, &out[i].first, &out[i].second);
        }
        //
        for(int i = 0; i < m; i++){
            bfs(out[i].first - 1, out[i].second - 1);
            for(int j = 0; j < m; j++){
                if(i == j) dis[i][j] = 0;
                else dis[i][j] = d[in[j].first - 1][in[j].second - 1];
            }
        }

        dpsolve();
    }
    return 0;
}
