struct edge{
    int to;
    int cost;
};

vector<edge> G[MAX_V];  //

int d[MAX_V];
bool vis[MAX_V];

void spfa(int s) 
{
    queue<int> q;
    memset(vis, 0, sizeof(vis));
    fill(d, d + V, INF);
    d[s] = 0;
    q.push(s);
    vis[s] = true;
    while (!q.empty()) 
    {
        int v = q.front(); 
        q.pop(); 
        vis[v] = false;
        for(int i = 0; i < G[v].size(); i++)
        {
            edge e = G[v][i];
            if (d[e.to] > d[v] + e.cost) 
            {
                d[e.to] = d[v] + e.cost;
                if (!vis[e.to])
                {
                    vis[e.to] = true;
                    q.push(e.to);
                }
            }
        }
    }
}
