    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            d[i][j] = inf;
        }
    }
    d[sx][sy] = 0;
    que.push(P(sx, sy));

    while(!que.empty()){
        P p = que.front();
        que.pop();
        for(int i = 0; i < 4; i++){
            int nx = p.first + dx[i];
            int ny = p.second + dy[i];
            if(nx >= 0 && nx < n && ny >= 0 && ny < n && mp[nx][ny] != '#' && d[nx][ny] == inf){

                d[nx][ny] = d[p.first][p.second] + 1;
                que.push(P(nx, ny));
            }
        }

    }
}

int main(){
    #ifndef ONLINE_JUDGE
        freopen("in.txt", "r", stdin);
    #endif // ONLINE_JUDGE
    while(~scanf("%d%d", &n, &m)){
        for(int i = 0; i < n; i++){
            scanf("%s", mp[i]);
        }
        for(int i = 0; i < m; i++){
            scanf("%d%d%d%d", &in[i].first, &in[i].second, &out[i].first, &out[i].second);
        }
        //
        for(int i = 0; i < m; i++){
            bfs(out[i].first - 1, out[i].second - 1);
            for(int j = 0; j < m; j++){
                if(i == j) dis[i][j] = 0;
                else dis[i][j] = d[in[j].first - 1][in[j].second - 1];
            }
        }

        dpsolve();
    }
    return 0;
}
