#include <bits/stdc++.h>

using namespace std;

const int maxn = 30;
int n, k;
int d[maxn];
int ans = 0;
int sum = 0;

bool is_prime(){
    if(sum < 2)
        return false;
    int tmp = sqrt(sum);
    for(int i = 2; i <= tmp; i++){
        if(!(sum % i)) 
            return false;
    }
    return true;
}

void dfs(int now, int dep){
    if(dep == k){
        if(is_prime())
            ans++;
        return;
    }
    if(now == n){
        return;
    }
    for(int i = now; i < n; i++){
        sum += d[i];
        dfs(i + 1, dep + 1);
        sum -= d[i];
    }
}

int main(){
