struct Ma
{
    int m[maxn][maxn];
};

Ma a,per;
int n, mod;

void init()//
{
    int i,j;
    for(i = 0; i < n; i++)
        for(j = 0; j < n; j++)
        {
            scanf("%d", &a.m[i][j]);
            a.m[i][j] %= mod;
            per.m[i][j] = (i == j);
        }
}

Ma add(Ma a,Ma b)//
{
    Ma c;
    for (int i = 0; i < n; ++i)
    {
        for (int j = 0; j < n; ++j)
        {
            c.m[i][j] = (a.m[i][j] + b.m[i][j]) % mod;
        }
    }
    return c;
}


Ma multi(Ma a,Ma b)//
{
    Ma c;
    for(int i = 0; i < n; i++)
        for(int j = 0; j < n; j++)
        {
            c.m[i][j] = 0;
            for(int k = 0; k < n; k++)
            {
                c.m[i][j] += a.m[i][k] * b.m[k][j];
            }
            c.m[i][j] %= mod;
        }
    return c;
}

Ma quick_pow(long long x)//
{
    Ma res = per;
    Ma p = a;
    while(x)
    {
        if(x&1) res = multi(res,p);
        p = multi(p,p);
        x >>= 1;
    }
    return res;
}
