typedef struct node{
    int vl;
    int vr;
    int id;
} nod;
nod a[maxn];
int d[2 * maxn];
int n, pn;
int ans[maxn];
//
bool cmp(const nod& a, const nod& b){
    if(a.vr == b.vr) 
        return a.vl < b.vl;
    else
        return a.vr > b.vr;
}
