int query(int a, int b, int k, int l, int r){
    if(a <= l && b >= r) return d[k];
    if(b <= l) return 0;
    else{
        int res1 = query(a, b, 2 * k + 1, l, (l + r) / 2);
        int res2 = query(a, b, 2 * k + 2, (l + r) / 2, r);
        return res1 + res2;
    }
}
//
void update(int k){
    k += n - 1;
    d[k] += 1;
    while(k > 0){
        k = (k - 1) / 2;
        d[k] = d[2 *k + 1] + d[2 * k + 2];
    }
}

