```C++
//
struct SegTree{
    int vl, vr;         //tree[k]vl, vr
    int mv;             //
}tree[maxn << 2];       //

void build(int k, int l, int r){
    tree[k].vl = l;
    tree[k].vr = r;
    if(l == r){
        tree[k].mv = d[l];
        return;
    }
    int mid = (l + r) / 2;
    build(k << 1, l, mid);
    build(k << 1 | 1, mid + 1, r);
    tree[k].mv = max(tree[k << 1].mv, tree[k << 1 | 1].mv);
}

void update(int k, int pos, int val){
    if(tree[k].vl == tree[k].vr){
        tree[k].mv = val;
        return;
    }
    int mid = (tree[k].vl + tree[k].vr) / 2;
    if(pos <= mid)
        update(k << 1, pos, val);
    else
        update(k << 1 | 1, pos, val);
    tree[k].mv = max(tree[k << 1].mv, tree[k << 1 | 1].mv); 
}

int query(int k, int l, int r){
    if(l <= tree[k].vl && r >= tree[k].vr)
        return tree[k].mv;
    int mid = (tree[k].vl + tree[k].vr) / 2;
    if(r <= mid)
        return query(k << 1, l, r);
    if(l > mid)
        return query(k << 1 | 1, l, r);
    return max(query(k << 1, l, mid), query(k << 1 | 1, mid + 1, r));

}

