```C++
#include <bits/stdc++.h>

using namespace std;

const int maxn = 2e5 + 7;
int n, m;
struct SegTree{
    int vl, vr;
    int mv;
}tree[maxn << 2];

int d[maxn];

void build(int k, int l, int r){
    tree[k].vl = l;
    tree[k].vr = r;
    if(l == r){
        tree[k].mv = d[l];
        return;
    }
    int mid = (l + r) / 2;
    build(k << 1, l, mid);
    build(k << 1 | 1, mid + 1, r);
    tree[k].mv = max(tree[k << 1].mv, tree[k << 1 | 1].mv);
}

void update(int k, int pos, int val){
    if(tree[k].vl == tree[k].vr){
        tree[k].mv = val;
        return;
    }
    int mid = (tree[k].vl + tree[k].vr) / 2;
    if(pos <= mid)//doubt
        update(k << 1, pos, val);
    else
        update(k << 1 | 1, pos, val);
    tree[k].mv = max(tree[k << 1].mv, tree[k << 1 | 1].mv); 
}

int query(int k, int l, int r){
    if(l <= tree[k].vl && r >= tree[k].vr)
        return tree[k].mv;
    int mid = (tree[k].vl + tree[k].vr) / 2;
    if(r <= mid)
        return query(k << 1, l, r);
    if(l > mid)
        return query(k << 1 | 1, l, r);
    return max(query(k << 1, l, mid), query(k << 1 | 1, mid + 1, r));

}

int main(){
    //ios::sync_with_stdio(false);
    //cin.tie(0);
    //cout.tie(0);
    #ifndef ONLINE_JUDGE
        freopen("in.txt", "r", stdin);
    #endif

    while(~scanf("%d%d", &n, &m)){
        char c[10];
        int a, b;
        for(int i = 1; i <= n; i++){
            scanf("%d", &d[i]);
        }
        build(1, 1, n);
        for(int i = 1; i <= m; i++){
            scanf("%s%d%d", c, &a, &b);
            if(c[0] == 'Q')
                printf("%d\n", query(1, a, b));
            else if(c[0] == 'U')
            update(1, a, b);
        }

	}

    return 0;
}
```

