    for(int i = 1; i <= n; i++)
        scanf("%d", d + i);
    build(1, 1, n);
    while(q--){
        scanf("%s", s);
        if(s[0] == 'Q'){
            scanf("%d%d", &a, &b);
            printf("%lld\n", query(1, a, b));
        }
        else if(s[0] == 'C'){
